package javareview;

public class javabitwiseOperatorExamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      System.out.println((1|0));//true or false = true
      System.out.println((1&0));//true and false  = false
      System.out.println((1^0));// true xor false = true | true xor true = false | false xor false = false
      System.out.println((~3));// ture not = false| false not = true

	}

}
